// YBZCurrency.js


    const conversionRate = 1 / 33; // 1 USD = 33 TRY

    // Fonksiyon: Sayfadaki TL değerlerini USD'ye çevirir
    function convertTLtoUSD() {
        // Sayfadaki tüm metin düğümlerini al
        const textNodes = getTextNodesIn(document.body);

        // Her bir metin düğümünde TL değerlerini bul ve USD'ye çevir
        textNodes.forEach(node => {
            let text = node.nodeValue;
            const regex = /(\d+(?:\.\d+)?)\s*TL/gi; // TL değerlerini bulmak için regex
            node.nodeValue = text.replace(regex, (match, p1) => {
                let tlValue = parseFloat(p1);
                let usdValue = (tlValue * conversionRate).toFixed(2);
                return `${usdValue} $`;
            });
        });
    }

    // Fonksiyon: Sayfadaki tüm metin düğümlerini alır
    function getTextNodesIn(element) {
        var textNodes = [];
        if (element) {
            for (element = element.firstChild; element; element = element.nextSibling) {
                if (element.nodeType === 3) {
                    textNodes.push(element);
                } else {
                    textNodes = textNodes.concat(getTextNodesIn(element));
                }
            }
        }
        return textNodes;
    }

    // Pluginin başlatılması
    function run() {
      convertTLtoUSD();
    }

